import React from "react"

import {Navbar} from "./my componenet/Navbar";
import { SearchItem } from "./SearchItem";
import { AddItem } from "./AddItem";
import {Main} from "./my componenet/Main";
import {Footer} from "./my componenet/footer";

import { useState } from "react";


export default function App() {
   
  // const [items, setItems ] =useState(JSON.parse(localStorage.getItem('shoppinglist')));
   const[newItem, setNewItem] =useState('')
const[search, setSearch]= useState('')




   const setAndSaveItems = (newItems) => {
  setItems(newItems);
    localStorage.setItem('shoppinglist', JSON.stringify(newItems));
   }

   const addItem = (item) => {
    const id =items.length ? items[items.length - 1]. id + 1: 1;
    const myNewItem = {id, checked:false, item};
    const listItems =  [...items,myNewItem];
  setAndSaveItems(listItems);
   
   }

    const handleCheck = (id) => {
        const listItems = items.map((item) => item.id === id? { ...item,
         checked: !item.checked } : item);
        setAndSaveItems(listItems);
     
        }
     
     const handleDelete =(id) => {
       const listItems = items.filter((item)=> item.id  !== id);
      setAndSaveItems(listItems);
      
     }
    
     const handleSubmit =(e) => {
      e.preventDefault();
      if (!newItem) return;
    addItem(newItem);
    setNewItem('');
      // addItem
      
     
     };


    return(
          <div>
        <Navbar title="Grocery List"/>
       
       <AddItem
       newItem={newItem}
       setNewItem={setNewItem}
       handleSubmit={handleSubmit}
       />
        <SearchItem
        search={search}
        setSearch={setSearch}
        />
        <Main
        items={items.filter(item => ((item.item).toLowerCase()).includes
        (search.toLowerCase()))}
        
        handleCheck={handleCheck}
        handleDelete={handleDelete}
        />
       
        <Footer length={items.length}/> {/* Add the Footer component here */}
      
        </div>
       

    );
}
