import React from "react"

import {Navbar} from "./my componenet/Navbar";
import { AddItem } from "./AddItem";
import {Main} from "./my componenet/Main";
import {Footer} from "./my componenet/footer";

import { useState } from "react";


export default function App() {
    const [items, setItems ] =useState([
        {
          id : 1,
          checked:true,
          item: "one half pound bag of cocoa covered ",
        },
        {
        id : 2,
        checked:false,
        item: "one half pound bag of cocoa covered "
      },
      { 
      id : 3,
        checked:true,
        item: "one half pound bag of cocoa covered "
    }
    ]);
   const[newItem, setNewItem] =useState('')

   const setAndSaveItems = (newItems) => {
  setAndSaveItems(newItems);
    localStorage.setItem('shoppinglist', JSON.stringify(newItems));
   }

   const addItem = (item) => {
    const id =items.length ? items[items.length - 1]. id + 1: 1
    const myNewItem = {id, chacked:false, item};
    const listItems =  [...items,myNewItem];
  setAndSaveItems(listItems);
   
   }

    const handleCheck = (id) => {
        const listItems = item.map((item) => item.id === id? { ...item,
         checked: !item.checked } : item);
        setAndSaveItems(listItems);
     
        }
     
     const handleDelete =(id) => {
       const listItems = item.filter((item)=> item.id  !== id);
      setAndSaveItems(listItems);
      
     }
    
     const handleSubmit =(e) => {
      e.preventDefault();
      if (!newItem) return;
    AddItem(newItem);
    setNewItem('');
      // addItem
      setNewItem('');
      console.log('submitted')
     }


    return(
          <div>
        <Navbar title="Grocery List"/>
       <AddItem
       newItem={newItem}
       setNewItem={setNewItem}
       handleSubmit={handleSubmit}
       />
        <Main
        item={item}
        
        handleCheck={handleCheck}
        handleDelete={handleDelete}
        />
       
        <Footer length={items.length}/> {/* Add the Footer component here */}
      
        </div>
       

    );
}
