import {Navbar} from "./my componenet/Navbar";
import { AddItem } from "./AddItem";
import {Main} from "./my componenet/Main";
import {Footer} from "./my componenet/footer";

import { useState } from "react";


export default function App() {
    const [item,setItems ] =useState([
        {
          id : 1,
          checked:true,
          item: "one half pound bag of cocoa covered "
        },
        {
        id : 2,
        checked:false,
        item: "one half pound bag of cocoa covered "
      },
      { 
      id : 3,
        checked:true,
        item: "one half pound bag of cocoa covered "
    }
    ]);
   const[newItem, setNewItem] =useState('')

   const addItem = (item) => {
    const id =items.length ? items[items.length - 1]. id + 1: 1
   }

    const handleCheck = (id) => {
        const listItems = item.map((item) => item.id === id? { ...item,
         checked: !item.checked } : item);
         setItems(listItems);
         localStorage.setItem('shoppinglist', JSON.stringify(listItems));
        }
     
     const handleDelete =(id) => {
       const listItems = item.filter((item)=> item.id  !== id);
       setItems(listItems);
       localStorage.setItem('shoppinglist', JSON.stringify(listItems));
      
     }
    
     const handleSubmit =(e) => {
      e.preventDefault();
      if (!newItem) return;
    
      // addItem
      setNewItem('');
      console.log('submitted')
     }


    return(
          <div>
        <Navbar title="Grocery List"/>
       <AddItem
       newItem={newItem}
       setNewItem={setNewItem}
       handleSubmit={handleSubmit}
       />
        <Main
        items={item}
        
        handleCheck={handleCheck}
        handleDelete={handleDelete}
        />
       
        <Footer length={item.length}/> {/* Add the Footer component here */}
      
        </div>
       

    );
}
